CREATE DATABASE book_database;

USE book_database;

CREATE TABLE IF NOT EXISTS `book_5000` (
    `ISBN` VARCHAR(10) CHARACTER SET utf8,
    `Book_Title` VARCHAR(156) CHARACTER SET utf8,
    `Book_Author` VARCHAR(53) CHARACTER SET utf8,
    `Year_Of_Publication` INT,
    `Publisher` VARCHAR(64) CHARACTER SET utf8,
    `Image_URL_S` VARCHAR(60) CHARACTER SET utf8,
    `Image_URL_M` VARCHAR(60) CHARACTER SET utf8,
    `Image_URL_L` VARCHAR(60) CHARACTER SET utf8
);


CREATE TABLE IF NOT EXISTS `user_5000` (
    `User_ID` INT,
    `Location` VARCHAR(31) CHARACTER SET utf8,
    `Age` VARCHAR(4) CHARACTER SET utf8
);


CREATE TABLE IF NOT EXISTS `rating_5000` (
    `User_ID` INT,
    `ISBN` VARCHAR(13) CHARACTER SET utf8,
    `Book_Rating` INT
);
